import React, { Component } from "react";
import "../CSS/donor.css";

import axios from "axios";

export default class addDonors extends Component {
  constructor(props) {
    super(props);

    this.state = {
      doctor: {},
      categories: [],

      name: "",
      contact: "",
      email: "",
      duration: "",
      experience: "",
      // category: [],
      category: "",
      file: null,
    };
    this.onChange = this.onChange.bind(this);
    this.handleDropdownChange = this.handleDropdownChange.bind(this);
  }
  componentDidMount = async () => {
    const token = sessionStorage.getItem("token");
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };
      const result = await axios.get(
        `http://localhost:5000/api/v1/doctor/me`,
        config
      );
      this.setState({
        doctor: result.data.data,
      });
      console.log(result.data.data);
      const res = await axios.get(
        "http://localhost:5000/api/v1/category/",
        config
      );
      this.setState({
        categories: res.data.data,
      });
      console.log(res.data.data);
    } catch (err) {
      // console.log("Can't load the items");
    }
  };

  // Input on change
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  // Dropdown change
  handleDropdownChange = (e) => {
    this.setState({ category: e.target.value });
    console.log(e.target.value);
  };
  // fileupload
  onChangeHandler = (e) => {
    this.setState({
      file: e.target.files[0],
    });
  };
  onSubmit = async (e) => {
    e.preventDefault();

    // const data = new FormData();
    // data.append("file", this.state.file, this.state.file.name);

    // console.log(data);
    // const token = sessionStorage.getItem("token");
    // const config = {
    //   headers: {
    //     Authorization: `Bearer ${token}`,
    //     "Content-Type": "multipart/form-data",
    //   },
    // };
    try {
      // const res = await axios.post(
      //   `http://localhost:5000/api/v1/doctor/photo`,
      //   data,
      //   config
      // );
      // console.log(res.data.data);

      const products = {
        name: this.state.name,
        phone: this.state.contact,
        specialization: this.state.category,
        email: this.state.email,
        duration: this.state.duration,
        experience: this.state.experience,
        fees: this.state.fees,
        image: res.data.data,
      };
      const body = JSON.stringify(products);
      console.log(body);
      const config1 = {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };
      const result = await axios.put(
        `http://localhost:5000/api/v1/doctor`,
        body,
        config1
      );
      console.log(result);
      alert(`Doctors Details Added ${result.data.data.name}`);
    } catch (err) {
      // console.log("Can't load the items");
    }
  };

  render() {
    const {
      duration,
      name,
      specialization,
      fees,
      experience,
      email,
      phone,
      image,
    } = this.state.doctor;
    return (
      <div className="container itmtop">
        <div className="">
          {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
          <div className="" id="login-second">
            <div className="page-wrapper p-t-50 p-b-50">
              <div className="wrapper wrapper--w900 ">
                <div className="card cardH card-6 bg-dark">
                  <div className="card-heading m-4 ">
                    <h2 className="title address3 text-dark">Doctors</h2>
                  </div>
                  <div className="card-body">
                    <form
                      onSubmit={this.onSubmit}
                      encType="multipart/form-data"
                    >
                      <div className="form-row frow">
                        <div className="name">Doctor Name:</div>
                        <div className="value">
                          <input
                            className="input--style-6"
                            type="text"
                            name="name"
                            placeholder={name}
                            value={this.state.name}
                            onChange={this.onChange}
                          />
                        </div>
                      </div>
                      <div className="form-row frow">
                        <div className="name">Specialization</div>
                        <div className="value">
                          <select
                            id="dropdown form-control"
                            className="btn bg-light"
                            onChange={this.handleDropdownChange}
                          >
                            <option value="nocat">Specialization</option>
                            {this.state.categories.map((category) => (
                              <option key={category._id} value={category._id}>
                                {category.catname}
                              </option>
                            ))}
                          </select>
                        </div>
                      </div>

                      <div className="form-row frow">
                        <div className="name">Experience</div>
                        <div className="value">
                          <div className="input-group">
                            <input
                              className="input--style-6"
                              type="text"
                              name="experience"
                              placeholder={experience}
                              value={this.state.experience}
                              onChange={this.onChange}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="form-row frow">
                        <div className="name">Duration</div>
                        <div className="value">
                          <div className="input-group">
                            <input
                              className="input--style-6"
                              type="text"
                              name="duration"
                              placeholder={duration}
                              value={this.state.duration}
                              onChange={this.onChange}
                            />
                          </div>
                        </div>
                      </div>

                      <div className="form-row frow">
                        <div className="name">Email</div>
                        <div className="value">
                          <div className="input-group">
                            <input
                              className="input--style-6"
                              type="email"
                              name="email"
                              placeholder={email}
                              value={this.state.email}
                              onChange={this.onChange}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="form-row frow">
                        <div className="name">Contact</div>
                        <div className="value">
                          <div className="input-group">
                            <input
                              className="input--style-6"
                              type="text"
                              name="contact"
                              placeholder={phone}
                              value={this.state.phone}
                              onChange={this.onChange}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="form-row frow">
                        <div className="name">Fees</div>
                        <div className="value">
                          <div className="input-group">
                            <input
                              className="input--style-6"
                              type="text"
                              name="fees"
                              placeholder={fees}
                              value={this.state.fees}
                              onChange={this.onChange}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="card-footer">
                        <a
                          className="btn btn--radius-2 btn-dark"
                          type="submit"
                          href=""
                        >
                          Submit
                        </a>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
