import React, { Component, Fragment } from "react";
import logo from "../../assets/logo.png";
import "../CSS/donor.css";
import ShowItems from "./showItems";
import axios from "axios";

class CompanyHome extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //   cat: "",
      doctor: [],
      //   specialization: "",
    };
    // this.onClickHandler = this.onClickHandler.bind(this);
  }

  componentDidMount = async () => {
    console.log(this.props.location.state.cat);

    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        ` http://localhost:5000/api/v1/category/${this.props.location.state.cat}/doctors`,
        config
      );
      this.setState({
        doctors: res.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  onClickHandler = (e) => {
    // this.setState({ category: e.target.value });
    console.log(e.target.name);
  };

  render() {
    console.log(this.props);
    return (
      <Fragment>
        {/* {/* End of Navbar */}

        {/* <section id="sectionF1">
          <div className="row container-fluid m-5 ">
            <div className="col-md-3">
              <div className="card p-3">
                <div class="card text-center">
                  <img class="card-img-top" src={logo} alt="" /> 
                  <i className="fa fa-book fa-5x "></i>
                  <div class="card-body">
                    <h4 class="card-title">Products</h4>
                    <p class="card-text">lists</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>  */}

        <section class="counts section-bg mt-5">
          <div className=" container-fluid mt-5" id="product">
            <h2> List Of Doctors</h2>

            <div className="row">
              {this.state.doctors.map((doc) => (
                <ShowItems
                  key={doc._id}
                  doctor={doc}
                  specialization={doc.specialization.catname}
                ></ShowItems>
              ))}
            </div>
          </div>
        </section>
      </Fragment>
    );
  }
}

export default CompanyHome;
