import React from "react";

import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import CompanyHome from "./CompanyHome";
// import fRegistration from "./Registration";
import CompanyNavbar from "./CompanyNavbar";
import Category from "./Category";
import axios from "axios";

function App() {
  state = {
    users: [],
    products: [],
    category: [],
  };

  getCategory = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        "http://localhost:5000/api/v1/category/",
        config
      );
      this.setState({
        category: res.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };

  getproducts = async (catid) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        ` http://localhost:5000/api/v1/category/${catid}/products`,
        config
      );
      this.setState({
        products: res.data.data,
      });
    } catch (err) {
      const res = await axios.get(
        ` http://localhost:5000/api/v1/products`,
        config
      );
      this.setState({
        products: res.data.data,
      });
      // console.log("Can't load the items");
    }
  };
  return (
    <Router>
      <div className="">
        <CompanyNavbar />
        {/* <div className="jumbotron" style={{ marginBottom: 0 + "px" }}></div> */}

        <Switch>
          <Route
            exact
            path={"/user/Home"}
            render={(props) => (
              <CompanyHome
                {...props}
                user={this.state.user}
                getproducts={this.getproducts}
                getCategory={this.getCategory}
                products={this.state.products}
                category={this.state.category}
              />
            )}
          />
          {/* <Route exact path={"/user/Home"} component={CompanyHome} /> */}
          <Route exact path={"/user/Category"} component={Category} />
          {/* <Route path={"/vendor/fsignup"} component={fRegistration} /> */}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
