import React, { Component, Fragment } from "react";
import logo from "../../assets/logo.png";
import "../CSS/donor.css";
import Category from "./Category";
import axios from "axios";

class CompanyHome extends Component {
  constructor(props) {
    super(props);
    this.state = { categories: [] };
  }

  componentDidMount = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        "http://localhost:5000/api/v1/category/",
        config
      );
      this.setState({
        categories: res.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  render() {
    return (
      <Fragment>
        {/* {/* End of Navbar */}

        {/* <section id="sectionF1">
          <div className="row container-fluid m-5 ">
            <div className="col-md-3">
              <div className="card p-3">
                <div class="card text-center">
                  <img class="card-img-top" src={logo} alt="" /> 
                  <i className="fa fa-book fa-5x "></i>
                  <div class="card-body">
                    <h4 class="card-title">Products</h4>
                    <p class="card-text">lists</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>  */}

        <section class="counts section-bg mt-5">
          <div class="container mt-5">
            <div class="row">
              <div
                class="col-lg-12 text-center animated fadeInUp wow animated"
                data-aos="fade-up"
                data-aos-delay="600"
              >
                <div>
                  <div class="row">
                    <div class=" col-lg-12 mb-50">
                      <h2> List of Category</h2>
                      <div className="row mt-5" id="category">
                        <div className=" grid-margin">
                          <div className="card card-right">
                            <div className="card-body">
                              <h4 className="card-title">Category</h4>{" "}
                              <div className="pull-right mb-2">
                                {/* <Link
                    to="/category"
                    className="btn btn-danger btn-sm"
                    title="Quick Shop"
                    data-toggle="modal"
                    data-target="#quickModel"
                  >
                    <i className="fa fa-plus fa-1x">Add Category</i>
                  </Link> */}

                                <div className="text-center">
                                  <button
                                    type="button"
                                    className="btn btn-danger "
                                    title="Quick Shop"
                                    data-toggle="modal"
                                    data-target="#quickModel"
                                  >
                                    <i className="fa fa-plus fa-1x">
                                      Add Category
                                    </i>
                                  </button>
                                </div>
                                {/* <button
                    type="button"
                    className="btn  m-1"
                    title="Quick Shop"
                    data-toggle="modal"
                    data-target="#quickModel"
                  >
                    <i className="fa fa-eye "></i>
                  </button> */}
                              </div>
                              <div className="table-responsive ">
                                <table className="table">
                                  <thead>
                                    <tr>
                                      <th> No. </th>
                                      <th> Name </th>

                                      <th> Action </th>
                                    </tr>
                                  </thead>
                                  <tbody>
                                    {this.state.categories.map((category) => (
                                      <tr key={category._id}>
                                        <td>{category.__v} </td>
                                        <td>{category.catname} </td>

                                        <td className="actions" data-th="">
                                          {/* <button className="btn btn-info btn-sm mr-4">
                              <i className="fa fa-edit"></i>
                            </button> */}
                                          <button
                                            className="btn btn-danger btn-sm"
                                            onClick={(e) =>
                                              this.onDeleteCategory(
                                                category._id,
                                                e
                                              )
                                            }
                                          >
                                            <i className="fa fa-trash-o"></i>
                                          </button>
                                        </td>
                                      </tr>
                                    ))}
                                  </tbody>
                                </table>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <Category />
          </div>
        </section>
      </Fragment>
    );
  }
}

export default CompanyHome;
